/**
 * A very basic parser
 *
 * @public
 */
export default class Parser {
    private _content;
    private _curr;
    constructor(_content: string);
    eof(): boolean;
    skipSpaces(): void;
    readonly index: number;
    readonly curr: string | undefined;
    readonly len: number;
    next(count?: number): string;
    nextIndex(character: string, checkEscape?: boolean, allowEnd?: boolean): number;
    prev(): string;
    iterate(fn: (character: string, idx: number) => boolean): void;
    eat(count: number): string;
    eatTo(index: number): string;
}
